svg.script:

var s = Snap('#snap');

// Lets create big circle in the middle:
var bigCircle = s.circle(150, 150, 100);
// By default its black, lets change its attributes
bigCircle.attr({
    fill: "#bada55",
    stroke: "#000",
    strokeWidth: 5
});  


// Writing text as simple as:
var text = s.group(bigCircle,s.text(150, 150, "Snap.svg"));
text.attr({
    fill: "#fff"
});
